package com.kctech.dayone;

public class Switchv {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int player = 4;
	    
		switch (player) {
	      case 1:
	        System.out.println("1st player V");
	        break;
	      case 2:
	        System.out.println("2st player I");
	        break;
	      case 3:
	        System.out.println("3st player G");
	        break;
	      case 4:
	        System.out.println("4st player N");
	        break;
	      case 5:
	        System.out.println("5st player E");
	        break;
	      case 6:
	        System.out.println("6st player S");
	        break;
	      case 7:
	        System.out.println("7st player H");
	        break;
	    }
	}

}
